{!REQUIRESCRIPT("/soap/ajax/31.0/apex.js")} 
var namespace_prefix = ''; 
var gettingnamespace = sforce.connection.query("SELECT NamespacePrefix FROM ApexClass WHERE Name='ApplicationUtilities' LIMIT 1"); 
var getname = gettingnamespace.getArray("records"); 
var className = "ScreenApplicationCase"; 
if(getname.length > 0) 
{ 
namespace_prefix = getname[0].NamespacePrefix; 
className = namespace_prefix + ".ScreenApplicationCase"; 
} 
var res=sforce.apex.execute(className,"Screen",{sAppId:"{!vlocity_ps__Application__c.Id}", sType:"{!vlocity_ps__Application__c.vlocity_ps__Type__c }"}); 
alert(res);
